INSERT INTO `songs` (`id`, `name`, `preview_text`, `detail_text`, `date_create`, `id_cat`, `picture`, `counter`, `youtube`) VALUES
(1, 'Nơi Ấy Con Tìm Về', 'Nguyễn Huy Hoàng về đích thứ 2 lượt bơi thứ 2 vòng loại 800m tự do nam.\r\nHuy Hoàng sẽ chờ 3 lượt bơi nữa để biết mình có nằm trong Top 8 lọt vào chung kết hay chưa.', '<p>Nguyễn Huy Ho&agrave;ng về đ&iacute;ch thứ 2 lượt bơi thứ 2 v&ograve;ng loại 800m tự do nam. Huy Ho&agrave;ng sẽ chờ 3 lượt bơi nữa để biết m&igrave;nh c&oacute; nằm trong Top 8 lọt v&agrave;o chung kết hay chưa.</p>\r\n', '2021-07-25 09:17:47', 1, 'f1af272d318a2aef209ce13f004a74f1_1367136050-1-206126351788800.jpg', 7, '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/RHAvrJed1I8\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>'),
(2, 'Càng Khó Càng Yêu', 'Nhớ lắm Tặng a món quà tinh thần ... \r\n01.04 Ngày kĩ niệm :x \r\nDù ngày mai đời đổi thay \r\nChỉ yêu anh và yêu thôi, \r\nNguyện yêu mãi muôn đời chẳng quên', '<p>Nhớ lắm Tặng a m&oacute;n qu&agrave; tinh thần ... 01.04 Ng&agrave;y kĩ niệm :x D&ugrave; ng&agrave;y mai đời đổi thay Chỉ y&ecirc;u anh v&agrave; y&ecirc;u th&ocirc;i, Nguyện y&ecirc;u m&atilde;i mu&ocirc;n đời chẳng qu&ecirc;n</p>\r\n', '2021-07-25 09:17:47', 2, 'erik-sau-tat-ca-1-1467793197-206115984896400.jpg', 12, '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/wEBgnZl9L0o\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>'),
(3, 'Ngày xưa em đến', 'Nhiệm vụ thích hợp cho nguyên team anh em đi vào thể hiện trình kỹ năng đây ! Chi tiết sự kiện bên dưới', '<p>Ho&agrave; trong cơn gi&oacute; đ&ocirc;ng bay qua thời gian<br />\r\nTừng đ&ocirc;i ch&acirc;n l&atilde;ng du bước tr&ecirc;n đường khuya<br />\r\nHạt mưa rơi lung linh như hạt ngọc đ&ecirc;m th&acirc;u<br />\r\nBao giấc mơ tan th&agrave;nh sương hoa<br />\r\nĐể giờ đ&acirc;y chơi vơi b&oacute;ng h&igrave;nh<br />\r\nmột lo&agrave;i hoa th&aacute;ng năm.<br />\r\n<br />\r\nChỉ anh với mỗi anh c&ocirc; đơn lạnh gi&oacute;<br />\r\nM&igrave;nh anh với nỗi đau khi em l&igrave;a xa<br />\r\nMột m&igrave;nh anh c&ocirc; đơn,đếm từng giọt sao rơi<br />\r\nXa qu&aacute; trong anh, ng&agrave;y m&igrave;nh đứng đ&oacute; b&ecirc;n nhau<br />\r\nĐ&ocirc;i tay em yếu mềm,đợi chờ mưa<br />\r\nrơi xuống cho mi&ecirc;n man mắt anh...<br />\r\n<br />\r\nNgười về đấy c&oacute; thấy gi&oacute; bay tr&ecirc;n m&ocirc;i kh&ocirc;ng<br />\r\nNgười về đ&acirc;y chắc c&oacute; nắng hong mi em kh&ocirc;?<br />\r\nChẳng th&agrave; l&agrave;m một ch&uacute;t hương thơm nhẹ bay<br />\r\ntr&ecirc;n t&oacute;c em cho ng&agrave;y th&ecirc;m ngoan<br />\r\nGọi bờ m&ocirc;i n&agrave;y khao kh&aacute;t&hellip;<br />\r\n<br />\r\nNắng c&oacute; thấy nhớ hương đ&ocirc;i m&ocirc;i em<br />\r\nV&agrave; l&agrave;n gi&oacute; c&oacute; luyến tiếc<br />\r\nng&agrave;y vội v&atilde; sẽ tr&ocirc;i qua mau,<br />\r\nĐợi chờ anh với anh giữa trời.<br />\r\nGiờ lại thấy cơn mưa rơi trong mắt nhạt nh&ograve;a.<br />\r\n<br />\r\nN&agrave;y nhớ h&aacute;t khẽ khẽ tr&ecirc;n m&ocirc;i thế th&ocirc;i<br />\r\nSợ rằng sớm mai khi giấc mơ kia bỗng t&agrave;n.<br />\r\n<br />\r\nChẳng t&igrave;m thấy được một lần nỗi nhớ<br />\r\nChẳng t&igrave;m thấy thấp tho&aacute;ng b&oacute;ng d&aacute;ng<br />\r\nd&ugrave; đ&atilde; tan vỡ, d&ugrave; mưa tan biến...<br />\r\nChẳng nhận ra đ&ocirc;i khi em l&agrave; một giọt mưa<br />\r\nL&agrave; một tho&aacute;ng m&acirc;y kh&oacute;i anh mơ...</p>\r\n', '2021-07-25 09:19:11', 3, 'bc0df0639c8d8fd3e36cb7795e356c48_1296095413-206103443761100.jpg', 6, '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/3K4WC1vw8Is\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>'),
(4, 'Đổi thay', '“Nhớ…tiếng mưa rơi ngày xưu…lúc đôi ta còn nhau, khi tình yêu… bắt đầu…….”', '<p>H&atilde;y thắp &aacute;nh s&aacute;ng trong hồn t&ocirc;i<br />\r\nKhi ưu tư đang chất đầy<br />\r\nV&igrave; t&igrave;nh em đổi thay<br />\r\nBao nhi&ecirc;u mơ ước đ&atilde; tan dần<br />\r\nCuộc t&igrave;nh đ&atilde; mất thật x&oacute;t xa ..<br />\r\n<br />\r\nT&ocirc;i như chơi vơi trong biển khơi<br />\r\nSao em ra đi kh&ocirc;ng quay lại<br />\r\nĐể l&ograve;ng t&ocirc;i n&aacute;t tan<br />\r\nCon tim kh&ocirc; h&eacute;o trong mỏi m&ograve;n<br />\r\nNhạc điệu &acirc;n &aacute;i nay c&ograve;n đ&acirc;u<br />\r\n<br />\r\nThật đớn đau tr&aacute;i ngang<br />\r\nL&ograve;ng t&ocirc;i mang c&ugrave;ng cội đ&aacute;<br />\r\nT&ocirc;i chết giữa ng&agrave;n khơi<br />\r\nGiọt đắng cay thấm tan v&agrave;o kh&ocirc;ng gian<br />\r\nHồn ta vỡ trăng t&agrave;n đời ngả nghi&ecirc;ng<br />\r\n<br />\r\nKhi t&ocirc;i trao em linh hồn t&ocirc;i<br />\r\nNhư ng&ocirc;i sao băng qua đ&ecirc;m d&agrave;i<br />\r\nVề nơi rất xa<br />\r\nT&ocirc;i đ&acirc;u biết đang bay về một h&agrave;nh tinh s&aacute;ng đầy dối gian<br />\r\nT&ocirc;i lang thang mong t&igrave;m em<br />\r\n&Ocirc;i cơn đau thương th&acirc;n r&atilde; rời m&agrave; đường kh&ocirc;ng lối đi<br />\r\nN&agrave;ng l&agrave; giấc mơ ki&ecirc;u kỳ<br />\r\nĐể hồn t&ocirc;i uống men rượu cay<br />\r\n<br />\r\nThật đớn đau tr&aacute;i ngang<br />\r\nL&ograve;ng t&ocirc;i mang c&ugrave;ng cội đ&aacute;<br />\r\nT&ocirc;i chết giữa ng&agrave;n khơi<br />\r\nGiọt đắng cay tan v&agrave;o kh&ocirc;ng gian<br />\r\nHồn ta vỡ trăng t&agrave;n đời ngả nghi&ecirc;ng</p>\r\n', '2021-07-25 09:19:11', 4, '1538549680324_640-206091183495700.jpg', 7, '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/RfAB6nYMG4g\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>'),
(5, 'Anh khác hay em khác', 'Bài này ở xứ ta cũng tập khá thuần thục và bà con ngoài biển cũng nhiều lần được thấy tận mắt. Nếu mọi người chưa quên thì quán bia chúng tôi cũng đã đăng video vài lần cho đồng bào chiêm ngưỡng', '<p>C&oacute; phải em hết y&ecirc;u anh rổi<br />\r\nEm hay n&oacute;i đi ngươi ơi<br />\r\nAnh v&ocirc; t&acirc;m hay l&agrave; anh đ&atilde; sai<br />\r\nM&agrave; dường như ta dần xa c&aacute;ch<br />\r\nEm n&oacute;i đ&ocirc;i ta kh&ocirc;ng hợp<br />\r\nEm n&oacute;i anh kh&ocirc;ng c&ograve;n như trước<br />\r\nNhững tại sao em ơi<br />\r\nNhững l&uacute;c ta b&ecirc;n nhau B&agrave;n tay em kh&ocirc;ng muốn &ocirc;m thật l&acirc;u<br />\r\nV&agrave; dường như anh hiểu ra một điều<br />\r\nEm đả kh&aacute;c xưa nhiều lắm<br />\r\nV&agrave; dường như anh hiểu ra một điều<br />\r\nEm đ&atilde; hết y&ecirc;u anh phải kh&ocirc;ng<br />\r\nVậy th&igrave; anh sẽ bước ra đi<br />\r\nTrả lại em những ng&agrave;y anh chưa đến<br />\r\nV&agrave; khi anh ra đi em sẽ nhận ra kh&ocirc;ng ai y&ecirc;u em như anh đ&atilde; từng y&ecirc;u</p>\r\n', '2021-07-25 09:20:01', 5, '1494842845652_640-206079794569700.jpg', 7, '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/ZTbKpBzL7hE\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>'),
(6, 'Yêu từ đâu mà ra', 'Thời gian vừa qua cộng đồng mạng đang dậy sóng với một cái tên hoàn toàn mới mẻ Liz Zpoet – giọng ca chính của ca khúc “Yêu từ đâu mà ra”. Ca khúc mang đậm chất Underground quyến rũ giữ vị trí #2 trong suốt 4 tuần liên tiếp trên Zingchart-realtime, đe dọa soán ngôi vương  “Thích thì đến” của Lê Bảo Bình.', '<p>Y&ecirc;u từ đ&acirc;u m&agrave; ra</p>\r\n\r\n<p>Khi chia xa v&ograve;ng tay của nhau chỉ khiến ta th&ecirc;m buồn đau hoho</p>\r\n\r\n<p>Y&ecirc;u cho nhau ngọt ng&agrave;o</p>\r\n\r\n<p>Nơi&hellip;m&ocirc;i h&ocirc;n rồi ch&agrave;o rồi ch&agrave;o</p>\r\n\r\n<p>Để sau lưng nhau l&agrave; chẳng lời n&agrave;o</p>\r\n\r\n<p>(No n&ocirc;)</p>\r\n\r\n<p>Y&ecirc;u cho th&ecirc;m niềm đau v&igrave; khi&hellip;y&ecirc;u ta đ&atilde; kh&ocirc;ng v&igrave; nhau ! (</p>\r\n\r\n<p>Để giờ ( no no )</p>\r\n\r\n<p>Nắng mang em đi rồi em để lại đớn đau mu&ocirc;n phần.</p>\r\n\r\n<p>Đ&atilde; Lỡ bu&ocirc;ng nhau r&atilde; rời.th&ocirc;i th&igrave; ta phải đ&agrave;nh c&aacute;ch xa.</p>\r\n\r\n<p>Nhớ nhau bao nhi&ecirc;u phần cũng đ&acirc;u thể t&igrave;m thấy nhau (uu)</p>\r\n\r\n<p>Nhớ lắm đ&ocirc;i vai em gầy</p>\r\n\r\n<p>C&ocirc; g&aacute;i ấy ở dưới g&oacute;c phố n&ecirc;n n&ecirc;n thơ love love you</p>\r\n\r\n<p>C&aacute;ch xa nhau l&agrave;m g&igrave; để giờ đ&acirc;y phải chịu đớn đau th&ecirc;m phần&hellip;.</p>\r\n\r\n<p>Nắng c&ograve;n vương vai đ&ecirc;</p>\r\n\r\n<p>Biết rằng ta sai</p>\r\n\r\n<p>Ta chẳng c&ograve;n tương lai</p>\r\n\r\n<p>Em nỡ l&ograve;ng y&ecirc;u ai&hellip;</p>\r\n\r\n<p>Cơn mưa rơi nghe hững hờ</p>\r\n\r\n<p>Em mang đến to&agrave;n bất ngờ</p>\r\n\r\n<p>C&oacute; ai.vẫn mong.c&oacute; tr&ocirc;ng.c&oacute; chờ</p>\r\n\r\n<p>Vẫn vương,C&oacute; mưa,</p>\r\n\r\n<p>Vẫn tr&ocirc;ng,Vẫn chờ</p>\r\n\r\n<p>Em ngang qua thật hững hờ</p>\r\n\r\n<p>Như tức nước anh vỡ bờ</p>\r\n\r\n<p>C&oacute; mơ,Thấy em,</p>\r\n\r\n<p>B&oacute;ng ai,</p>\r\n\r\n<p>Nhớ em,</p>\r\n\r\n<p>Nhớ c&acirc;u,</p>\r\n\r\n<p>Love you</p>\r\n\r\n<p>Miss you</p>\r\n\r\n<p>Từng hẹn thề</p>\r\n\r\n<p>Nhớ đ&ocirc;i vai em gầy c&ocirc; g&aacute;i ấy ở dưới g&oacute;c phố n&ecirc;n thơ</p>\r\n\r\n<p>Lỡ y&ecirc;u em như vầy . Nhưng tại sao n&agrave;ng lại ph&aacute; n&aacute;t cơn mơ &hellip;.</p>\r\n\r\n<p>Biết y&ecirc;u sao cho vừa . v&igrave; t&igrave;nh y&ecirc;u n&agrave;ng đ&acirc;u c&oacute; muốn răng tơ</p>\r\n\r\n<p>Thế gian t&igrave;nh l&agrave; g&igrave; m&acirc;y m&ugrave; răng đường khuya khuất lối đ&ocirc;i ta&hellip;</p>\r\n\r\n<p>Hỏi thế gian t&igrave;nh l&agrave; g&igrave;</p>\r\n\r\n<p>Bỏ hết thế giới l&agrave; v&igrave; n&agrave;ng</p>\r\n\r\n<p>khiến anh lu&ocirc;n thẫn thờ</p>\r\n\r\n<p>Ch&iacute;nh anh cũng bất ngờ</p>\r\n', '2021-07-27 04:22:27', 1, 'thumb_660_a421d991-5849-4789-a7d9-4ced65d508c3-206067873314400.jpg', 7, '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/EUGjrBnrheM\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>'),
(15, 'Chúng ta dừng lại ở đây thôi', 'Ở bản danh sách 31 cầu thủ ĐT Việt Nam hội quân vào ngày 5/8 tới đây, tiền đạo Nguyễn Công Phượng bất ngờ vắng mặt. ', '<p>Anh nghĩ ch&uacute;ng ta n&ecirc;n dừng lại nơi đ&acirc;y<br />\r\nCố gắng n&iacute;u k&eacute;o chẳng được g&igrave;<br />\r\nAnh biết cả hai cũng chẳng c&ograve;n y&ecirc;u thương<br />\r\nXa xỉ những lời khi&ecirc;m nhường.<br />\r\n<br />\r\nKhi hết y&ecirc;u nhau chỉ c&ograve;n lại b&oacute;ng tối<br />\r\nHai đứa chẳng muốn trao cơ hội<br />\r\nAnh n&oacute;i đ&ocirc;i c&acirc;u m&igrave;nh nằm lại b&ecirc;n nhau<br />\r\nNhưng cũng chỉ khiến ta th&ecirc;m nhức đầu.<br />\r\n<br />\r\nH&ocirc;m nay ta sẽ chia tay, anh xin b&ecirc;n nhau lần cuối<br />\r\nY&ecirc;u em hơn, quan t&acirc;m hơn ng&agrave;y mai<br />\r\nHai ch&uacute;ng ta sẽ kh&ocirc;ng gặp nhau<br />\r\nSẽ chẳng c&ograve;n l&agrave; của nhau.<br />\r\n<br />\r\n[ĐK:]<br />\r\nLần đầu anh thấy anh yếu l&ograve;ng<br />\r\nLần đầu ti&ecirc;n anh thấy em biết tr&acirc;n trọng<br />\r\nV&agrave;i giọt nước mắt đ&atilde; rơi &acirc;m thầm<br />\r\nCh&uacute;ng ta đều giấu.<br />\r\n<br />\r\nRồi cũng đến l&uacute;c đưa em về<br />\r\nChạy qua con phố vẫn nhớ thương về<br />\r\nCh&agrave;o nhau c&acirc;u cuối lặng lẽ nh&igrave;n nhau<br />\r\nCh&uacute;ng ta dừng lại ở đ&acirc;y th&ocirc;i.</p>\r\n', '2021-07-31 09:02:11', 1, 'Rosa_chinensis_petals-206050100634300.jpg', 6, '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/rPEhb8FxfMI\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>'),
(18, 'Tạm biệt nhé', 'Tạm biệt, tạm biệt lúc ta đi với nhau khi tan trường\r\nTạm biệt, giờ tạm biệt mãi là người bạn thân nhé\r\nTạm biệt, tạm biệt xa rồi bạn đừng quên tôi\r\nXin chào tạm biệt, giờ tạm biệt\r\nTạm biệt nhé nơi này bye bye.', '<p>&nbsp;Sẽ đến l&uacute;c phải n&oacute;i tạm biệt nơi đ&acirc;y<br />\r\nTiếc nuối bao ng&acirc;y thơ của một thời<br />\r\nNg&agrave;y mới tới lớp xa lạ, kh&ocirc;ng quen thuộc<br />\r\nL&agrave;m quen mới thấy vui.<br />\r\n<br />\r\nNg&agrave;y th&aacute;ng tr&ocirc;i qua kh&ocirc;ng kịp đếm<br />\r\nQua đi bao nhi&ecirc;u vui buồn n&agrave;o ai hay<br />\r\nMột thời hồn nhi&ecirc;n mơ mộng, nhiều vu vơ<br />\r\nH&atilde;y lưu lại s&acirc;u trong tr&aacute;i tim m&igrave;nh.<br />\r\n<br />\r\nLang thang đi tr&ecirc;n s&acirc;n trường vắng<br />\r\nNhặt c&agrave;nh phượng hồng c&ograve;n vương nơi n&agrave;y<br />\r\nTạm biệt từng lớp học buồn giờ chia tay rồi<br />\r\nCho t&ocirc;i y&ecirc;u th&ecirc;m nơi n&agrave;y một ch&uacute;t, một ch&uacute;t th&ocirc;i<br />\r\nĐể t&ocirc;i nhớ mai xa rồi sẽ nhớ nhau thật nhiều.</p>\r\n', '2021-08-05 03:01:12', 1, 'maxresdefault-206035237074800.jpg', 2, '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/6dbUgMD1vxU\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>'),
(19, 'Mưa', 'Mưa là một hiện tượng tự nhiên, xảy ra do sự ngưng tụ của hơi nước trên bầu trời, dưới dạng những đám mây, khi gặp điều kiện thích hợp, tạo thành giọt nước, nặng hơn không khí, và rơi xuống mặt đất, tạo thành cơn mưa.', '<ul>\r\n	<li>Đang ung dung tr&ecirc;n trời</li>\r\n	<li>Đạp m&acirc;y xanh h&ocirc;m nay xuống chơi</li>\r\n	<li>Anh h&ocirc;m nay hơi buồn</li>\r\n	<li>Thấy em tự nhi&ecirc;n thảnh thơi</li>\r\n	<li>Em đang c&ocirc; đơn</li>\r\n	<li>Th&igrave; b&agrave;n tay đ&acirc;u đưa đ&acirc;y</li>\r\n	<li>Th&ocirc;i coi như cho qua năm sau</li>\r\n	<li>Đ&aacute;m cưới liền tay</li>\r\n	<li>Đ&oacute; ngay m&ugrave;ng 2 c&oacute; ai đi nhiều</li>\r\n	<li>Em ơi em ở đ&acirc;u</li>\r\n	<li>Anh mang trầu cau qua</li>\r\n	<li>Anh rước d&acirc;u lu&ocirc;n</li>\r\n	<li>Mai đ&acirc;y ta sang gi&agrave;u</li>\r\n	<li>Qu&aacute; x&aacute; to thịt kho rau muống</li>\r\n	<li>Thắm thiết sẽ mặn nồng</li>\r\n</ul>\r\n', '2021-08-05 03:08:35', 5, 'maxresdefault (8)-206022320951900.jpg', 5, '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/-yhqOX3bLvQ\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>'),
(23, 'Cơn mưa ngang qua', 'Bạn thích được yêu thương, đối xử như một thành viên trong gia đình. Được cùng nhau ăn, cùng nhau cười, cùng nhau tham gia các công tác từ thiện cộng đồng, sống có ích hơn.', '<p>Cơn mưa ngang qua, cơn... cơn mưa ngang qua<br />\r\nCơn mưa ngang qua, cơn... cơn mưa ngang qua<br />\r\nCơn mưa ngang qua, cơn... cơn mưa ngang qua<br />\r\n(Ngang qua... ngang qua... ngang qua)<br />\r\nCơn... cơn mưa... cơn mưa ngang qua.<br />\r\n<br />\r\nCơn gi&oacute; r&iacute;t l&agrave;m anh t&ecirc; t&aacute;i một m&igrave;nh anh mang (oh oh cuộc t&igrave;nh anh mang)<br />\r\nChợt một niềm đau trong tim h&eacute;o &uacute;a gạt bỏ ước mơ (oh oh oh oh)<br />\r\nCố x&oacute;a hết m&agrave;n đ&ecirc;m u tối l&agrave;m nh&ograve;e đi trang giấy kia<br />\r\nV&agrave; bu&ocirc;ng lấp những giọt nước mắt ẩn trong tr&aacute;i tim (yeah ih yeah yeah)<br />\r\n<br />\r\nKh&ocirc;ng gian m&ugrave; mịt giờ b&ugrave; với t&acirc;m hồn anh<br />\r\nChẳng c&ograve;n niềm vui v&agrave; những điều ho&agrave;i b&atilde;o<br />\r\nD&ugrave; ng&agrave;y th&aacute;ng của cuộc đời sẽ d&agrave;y x&eacute;o l&agrave;m ngạt thở<br />\r\nĐốt bờ m&ocirc;i b&agrave;n tay của anh.<br />\r\n<br />\r\nBởi v&igrave; ai m&agrave; con người trong anh đ&atilde; qu&aacute; yếu đuối trước những đớn đau<br />\r\nĐừng rời xa em ơi v&igrave; anh kh&ocirc;ng thể tự đứng l&ecirc;n.<br />\r\n<br />\r\n[ĐK:]<br />\r\nUh... Anh kh&ocirc;ng tin v&agrave;o điều đ&oacute; (cơn mưa ngang qua)<br />\r\nAnh kh&ocirc;ng thể sống thiếu (nụ cười em đ&acirc;u)<br />\r\nAnh sẽ phải ra sao... nếu như b&ecirc;n anh kh&ocirc;ng c&ograve;n em (wow)<br />\r\n<br />\r\nCho anh lời n&oacute;i đ&oacute; (khi b&igrave;nh minh l&ecirc;n)<br />\r\nSẽ đợi c&acirc;u h&aacute;t (mong chờ em th&ocirc;i)<br />\r\nHiện tại v&agrave; qu&aacute; khứ... chẳng c&ograve;n lại g&igrave; đẹp đẽ hơn<br />\r\nAnh sẽ m&atilde;i ch&igrave;m trong đau khổ....ahhh</p>\r\n', '2021-08-06 10:56:28', 1, 'maxresdefault (7)-205990909005100.jpg', 8, '<iframe width=\"560\" height=\"315\" src=\"https://www.youtube.com/embed/TBOJNYGYUMw\" title=\"YouTube video player\" frameborder=\"0\" allow=\"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture\" allowfullscreen></iframe>'),
(24, 'Ngày ấy bạn và tôi', '#NgayAyBanVaToi #LynkLee #NhacHocSinh\r\n➢ Click theo dõi YouTube Lynk Lee: https://goo.gl/Gsv9BY \r\n➢ Official Facebook: https://www.facebook.com/Singer.LynkLee\r\n➢ Google Plus: https://plus.google.com/+lynklee88off...\r\n➢ Lynk Lee MV Playlist: https://goo.gl/15Oy6V \r\n\r\nPhòng thu Oreka: http://orekastudio.vn/phong-thu-am.html\r\n                           http://www.facebook.com/OrekaStudio\r\nNghe mp3: http://mp3.zing.vn/bai-hat/Ngay-Ay-Ba...\r\n\r\nNghe album: http://mp3.zing.vn/album/Ngay-Ay-Ban-...\r\n\r\nDownload mp3 320kbps: http://www.mediafire.com/?52fdl87m6xl...\r\n\r\nNgày ấy bạn và tôi lynk lee', '<p>Thời gian dần tr&ocirc;i c&ugrave;ng với tuổi thơ<br />\r\nNg&agrave;y xưa hồn nhi&ecirc;n c&ugrave;ng bao ước mơ<br />\r\nV&agrave; khi t&ocirc;i c&aacute;ch xa th&aacute;ng năm học tr&ograve;<br />\r\nC&ograve;n nhớ ng&agrave;y chia tay ấy buồn</p>\r\n\r\n<p>Ngo&agrave;i s&acirc;n phượng rơi hồng trang kỷ niệm<br />\r\nChợt nhớ về những ng&agrave;y xưa đ&atilde; xa<br />\r\nV&agrave; h&ocirc;m nay ch&uacute;ng ta đ&atilde; quay trở về<br />\r\nBạn ơi, giờ đ&acirc;y t&ocirc;i muốn n&oacute;i</p>\r\n\r\n<p>Rằng l&ograve;ng t&ocirc;i vẫn nhớ<br />\r\nNgười thầy ng&agrave;y xưa dạy ch&uacute;ng t&ocirc;i kh&ocirc;ng c&ograve;n<br />\r\nTrở lại nơi chốn ấy<br />\r\nTừng năm th&aacute;ng chợt hiện trong k&yacute; ức</p>\r\n\r\n<p>Ng&agrave;y xưa đ&oacute; m&atilde;i m&atilde;i bay xa, xa rời ta về nơi phương trời<br />\r\nC&ugrave;ng bao c&agrave;nh hoa phượng rơi ngập đầy nắng v&agrave;ng<br />\r\nV&agrave; những chiều mưa, ta c&ugrave;ng bước tr&ecirc;n đường xưa</p>\r\n\r\n<p>Ng&agrave;y xưa đ&oacute; m&atilde;i m&atilde;i bay xa, xa rời ta về nơi chốn ấy<br />\r\nBao bạn th&acirc;n khi xưa b&ecirc;n nhau<br />\r\nGiờ đ&acirc;y c&ograve;n lại m&igrave;nh ta trong nỗi nhớ thiết tha</p>\r\n\r\n<p>Bao nhi&ecirc;u y&ecirc;u thương giấc mơ<br />\r\nĐ&atilde; đi xa về nơi tr&aacute;i tim<br />\r\nV&agrave; c&ograve;n lại trong ta những th&aacute;ng năm d&agrave;i<br />\r\nNhớ về những thơ ấu kỷ niệm đ&atilde; xa từ l&acirc;u</p>\r\n\r\n<p>Giờ c&ograve;n nụ hồng khi xưa ta tặng bạn<br />\r\n&Eacute;p trong trang vở lưu b&uacute;t ấy<br />\r\nThế gian c&oacute; đổi thay, th&aacute;ng năm c&oacute; phai<br />\r\nTa sẽ m&atilde;i nhớ đến, nhớ đến nhau</p>\r\n\r\n<p>Rằng l&ograve;ng t&ocirc;i vẫn nhớ<br />\r\nNgười thầy ng&agrave;y xưa dạy ch&uacute;ng t&ocirc;i kh&ocirc;ng c&ograve;n<br />\r\nTrở lại nơi chốn ấy<br />\r\nTừng năm th&aacute;ng chợt hiện trong k&yacute; ức</p>\r\n\r\n<p>Ng&agrave;y xưa đ&oacute; m&atilde;i m&atilde;i bay xa, xa rời ta về nơi phương trời<br />\r\nC&ugrave;ng bao c&agrave;nh hoa phượng rơi ngập đầy nắng v&agrave;ng<br />\r\nV&agrave; những chiều mưa, ta c&ugrave;ng bước tr&ecirc;n đường xưa</p>\r\n\r\n<p>Ng&agrave;y xưa đ&oacute; m&atilde;i m&atilde;i bay xa, xa rời ta về nơi chốn ấy<br />\r\nBao bạn th&acirc;n khi xưa b&ecirc;n nhau<br />\r\nGiờ đ&acirc;y c&ograve;n lại m&igrave;nh ta trong nỗi nhớ ấy</p>\r\n\r\n<p>Đ&acirc;u, đ&acirc;u, biết đ&acirc;u rồi?<br />\r\nNg&agrave;y bạn v&agrave; t&ocirc;i vẫn thường đến lớp<br />\r\n(Đ&acirc;u, đ&acirc;u, đ&acirc;u)<br />\r\nGiờ t&igrave;m nơi đ&acirc;u?<br />\r\nC&ograve;n lại đ&acirc;y ch&uacute;t nắng hồng ng&acirc;y ng&ocirc; bằng lăng t&iacute;m</p>\r\n\r\n<p>Vẫn nhớ ho&agrave;i những ng&agrave;y thơ ấu<br />\r\nTa vui chơi dưới s&acirc;n trường<br />\r\nNhưng giờ đ&acirc;y xa nhau mất rồi<br />\r\nBạn th&acirc;n ơi chớ qu&ecirc;n</p>\r\n\r\n<p>Ng&agrave;y xưa đ&oacute; m&atilde;i m&atilde;i bay xa, xa rời ta về nơi phương trời<br />\r\nC&ugrave;ng bao c&agrave;nh hoa phượng rơi ngập đầy nắng v&agrave;ng<br />\r\nV&agrave; những chiều mưa, ta c&ugrave;ng bước tr&ecirc;n đường xưa</p>\r\n\r\n<p>Ng&agrave;y xưa đ&oacute; m&atilde;i m&atilde;i bay xa, xa rời ta về nơi chốn ấy<br />\r\nBao bạn th&acirc;n khi xưa b&ecirc;n nhau<br />\r\nGiờ đ&acirc;y c&ograve;n lại m&igrave;nh ta trong nỗi nhớ thiết tha</p>\r\n\r\n<p>Gi